// Publish to TikTok via Content Posting API.
// Supports both photo (PULL_FROM_URL) and video (PULL_FROM_URL).

const post = $input.first().json;
const accessToken = post.access_token || '';
const text = post.commentary || '';
const imageUrl = post.image_url || '';
const videoUrl = post.video_url || '';
const images = post.images || [];
const callbackUrl = post.callback_url || '';
const contentId = post.content_id || '';
const sharedSecret = $env.N8N_SHARED_SECRET || '';

let publishSuccess = false;
let publishId = '';
let publishError = '';

console.log('[TikTok-scheduled] video_url:', videoUrl ? videoUrl.substring(0, 80) : 'none');
console.log('[TikTok-scheduled] image_url:', imageUrl ? imageUrl.substring(0, 80) : 'none');
console.log('[TikTok-scheduled] images count:', images.length);

try {
  if (videoUrl) {
    // ── Video publish ──
    const videoBody = {
      post_info: {
        title: text.slice(0, 2200),
        privacy_level: 'SELF_ONLY',
        disable_comment: false
      },
      source_info: {
        source: 'PULL_FROM_URL',
        video_url: videoUrl
      }
    };

    const res = await fetch('https://open.tiktokapis.com/v2/post/publish/video/init/', {
      method: 'POST',
      headers: {
        'Authorization': 'Bearer ' + accessToken,
        'Content-Type': 'application/json; charset=UTF-8'
      },
      body: JSON.stringify(videoBody)
    });

    const json = await res.json();
    console.log('[TikTok-scheduled] Video response:', JSON.stringify(json).substring(0, 500));

    if (res.ok && (!json.error?.code || json.error.code === 'ok')) {
      publishSuccess = true;
      publishId = json.data?.publish_id || '';
    } else {
      publishError = json.error?.message || json.error?.code || 'TikTok video error ' + res.status;
    }
  } else if (imageUrl || images.length > 0) {
    // ── Photo publish ──
    const photoUrls = images.length > 0 ? images : [imageUrl];

    const photoBody = {
      post_info: {
        title: text.slice(0, 90),
        description: text.slice(0, 4000),
        privacy_level: 'SELF_ONLY',
        disable_comment: false,
        auto_add_music: true
      },
      source_info: {
        source: 'PULL_FROM_URL',
        photo_cover_index: 0,
        photo_images: photoUrls.slice(0, 35)
      },
      post_mode: 'DIRECT_POST',
      media_type: 'PHOTO'
    };

    const res = await fetch('https://open.tiktokapis.com/v2/post/publish/content/init/', {
      method: 'POST',
      headers: {
        'Authorization': 'Bearer ' + accessToken,
        'Content-Type': 'application/json; charset=UTF-8'
      },
      body: JSON.stringify(photoBody)
    });

    const json = await res.json();
    console.log('[TikTok-scheduled] Photo response:', JSON.stringify(json).substring(0, 500));

    if (res.ok && (!json.error?.code || json.error.code === 'ok')) {
      publishSuccess = true;
      publishId = json.data?.publish_id || '';
    } else {
      publishError = json.error?.message || json.error?.code || 'TikTok photo error ' + res.status;
    }
  } else {
    publishError = 'TikTok requires at least one image or video';
  }
} catch (e) {
  publishError = e.message || 'tiktok_publish_exception';
}

// Callback to Tipote
try {
  await fetch(callbackUrl, {
    method: 'POST',
    headers: {
      'X-N8N-Secret': sharedSecret,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      content_id: contentId,
      platform: 'tiktok',
      success: publishSuccess,
      postId: publishId,
      error: publishError || undefined
    })
  });
} catch (e) {
  // Callback failed — non-blocking
}

return [{
  json: {
    content_id: contentId,
    success: publishSuccess,
    publishId: publishId,
    error: publishError || null
  }
}];
